#include "Vex_Competition_Includes.c"

int drive = 0;

//basic stuff
void LDrive(int pwr)
{
	motor[FLDrive] = motor[BLDrive] = pwr;
}
void RDrive(int pwr)
{
	motor[FRDrive] = motor[BRDrive] = pwr;
}

//Drive Funcs
void arcade(int mov, int rot)
{
	LDrive(mov + rot);
	RDrive(mov - rot);
}
void tank(int left, int right)
{
	LDrive(left);
	RDrive(right);
}

