task usercontrol()
{
	while(true)
	{
		if(SensorValue[bump] == 1)
		{
			while(SensorValue[bump] == 1)
			{

			}
			drive++;
		}
		if(drive>=5)
		{
			drive=0;
		}
		if(drive == 0)
		{
			arcade(vexRT[Ch2], vexRT[Ch1]);
		}
		else if(drive == 1)
		{
			arcade(vexRT[Ch2], vexRT[Ch4]);
		}
		else if(drive == 2)
		{
			arcade(vexRT[Ch3], vexRT[Ch4]);
		}
		else if(drive == 3)
		{
			arcade(vexRT[Ch3], vexRT[Ch1]);
		}
		else if(drive == 4)
		{
			tank(vexRT[Ch3], vexRT[Ch2]);
		}
	}
}
